@import "tailwindcss";

@layer base {
  :root {
    --background: 0 0% 100%;
    --foreground: 222 47% 11%;
    --card: 0 0% 100%;
    --card-foreground: 222 47% 11%;
    --popover: 0 0% 100%;
    --popover-foreground: 222 47% 11%;
    --primary: 217 100% 50%;
    --primary-foreground: 0 0% 100%;
    --secondary: 210 66% 66%;
    --secondary-foreground: 222 47% 11%;
    --muted: 210 40% 96%;
    --muted-foreground: 215 16% 47%;
    --accent: 195 100% 50%;
    --accent-foreground: 222 47% 11%;
    --destructive: 0 84% 60%;
    --destructive-foreground: 0 0% 100%;
    --border: 214 32% 91%;
    --input: 214 32% 91%;
    --ring: 217 100% 50%;
    --radius: 0.75rem;

    /* Custom blue theme colors */
    --blue-50: 214 100% 97%;
    --blue-100: 214 95% 93%;
    --blue-200: 213 97% 87%;
    --blue-300: 212 96% 78%;
    --blue-400: 213 94% 68%;
    --blue-500: 217 91% 60%;
    --blue-600: 221 83% 53%;
    --blue-700: 224 76% 48%;
    --blue-800: 226 71% 40%;
    --blue-900: 224 64% 33%;
    --blue-950: 226 57% 21%;
  }

  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground;
    font-feature-settings: "rlig" 1, "calt" 1;
  }
}

@layer utilities {
