"use client";

import { HeroSection } from "@/components/landing/HeroSection";
import { ModulesShowcase } from "@/components/landing/ModulesShowcase";

export default function Home() {
  const scrollToModules = () => {
    document.getElementById("modules")?.scrollIntoView({ behavior: "smooth" });
  };

  return (
    <main className="relative">
      <HeroSection onScrollClick={scrollToModules} />
      <ModulesShowcase />
    </main>
  );
}

"use client";

import { useRef } from "react";
import Link from "next/link";
import { motion, useInView } from "framer-motion";
import { BookOpen, TrendingUp, DollarSign, PieChart, Briefcase, Target } from "lucide-react";
import { Card, CardContent, CardTitle, CardDescription } from "@/components/ui/card";

const modules = [
  {
    id: "accounting",
    title: "Accounting",
    description: "Master fundamental accounting concepts and financial statements",
    icon: BookOpen,
    gradient: "from-blue-500 to-blue-600",
  },
  {
    id: "valuation",
    title: "Valuation",
    description: "Learn valuation methodologies and techniques",
    icon: TrendingUp,
    gradient: "from-purple-500 to-purple-600",
  },
  {
    id: "ev-vs-equity",
    title: "Enterprise Value vs Equity Value",
    description: "Understand the bridge between EV and equity value",
    icon: PieChart,
    gradient: "from-green-500 to-green-600",
  },
  {
    id: "dcf",
    title: "DCF",
    description: "Master discounted cash flow analysis",
    icon: DollarSign,
    gradient: "from-orange-500 to-orange-600",
  },
  {
    id: "ma",
    title: "M&A",
    description: "Learn mergers and acquisitions concepts",
    icon: Briefcase,
    gradient: "from-red-500 to-red-600",
  },
  {
    id: "lbo",
    title: "LBO",
    description: "Understand leveraged buyout modeling",
    icon: Target,
    gradient: "from-indigo-500 to-indigo-600",
  },
];

function ModuleCard({ module, index }: { module: typeof modules[0]; index: number }) {
  const ref = useRef(null);
  const isInView = useInView(ref, { once: true, margin: "-100px" });
  const Icon = module.icon;

  return (
    <motion.div
      ref={ref}
      initial={{ opacity: 0, scale: 0.8, y: 50 }}
      animate={isInView ? { opacity: 1, scale: 1, y: 0 } : {}}
      transition={{
        duration: 0.5,
        delay: index * 0.1,
        type: "spring",
        stiffness: 100,
        damping: 15
      }}
    >
      <Link href={`/module/${module.id}`} className="group block h-full">
        <Card className="h-full border-2 hover:border-blue-300 transition-all duration-300 bg-white/80 backdrop-blur-sm card-hover overflow-hidden relative">
          {/* Gradient overlay on hover */}
          <div className="absolute inset-0 bg-gradient-to-br from-blue-500/0 to-blue-500/0 group-hover:from-blue-500/5 group-hover:to-cyan-500/5 transition-all duration-300"></div>

          <CardContent className="p-4 sm:p-6 relative z-10">
            <motion.div
              whileHover={{ scale: 1.1, rotate: 5 }}
              transition={{ type: "spring", stiffness: 300 }}
              className={`w-12 h-12 sm:w-14 sm:h-14 rounded-xl bg-gradient-to-br ${module.gradient} mb-4 flex items-center justify-center shadow-lg`}
            >
              <Icon className="w-7 h-7 text-white" />
            </motion.div>
            <CardTitle className="text-xl sm:text-2xl mb-3 group-hover:text-blue-600 transition-colors">
              {module.title}
            </CardTitle>
            <CardDescription className="text-slate-600 leading-relaxed">
              {module.description}
            </CardDescription>

            {/* Arrow indicator */}
            <div className="mt-4 flex items-center text-blue-600 font-medium opacity-0 group-hover:opacity-100 transition-opacity duration-300">
              <span className="text-sm">Start Learning</span>
              <svg className="w-4 h-4 ml-2 group-hover:translate-x-1 transition-transform" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
              </svg>
            </div>
          </CardContent>
        </Card>
      </Link>
    </motion.div>
  );
}

export function ModulesShowcase() {
  const sectionRef = useRef(null);
  const isInView = useInView(sectionRef, { once: true, margin: "-200px" });

  return (
    <section ref={sectionRef} id="modules" className="py-12 sm:py-16 md:py-24 bg-gradient-to-br from-blue-50 via-white to-blue-50">
      <div className="container mx-auto px-4">
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          animate={isInView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.6 }}
          className="text-center mb-16"
        >
          <h2 className="text-3xl sm:text-4xl md:text-5xl font-bold text-slate-900 mb-4">
            Master <span className="bg-gradient-to-r from-blue-600 to-cyan-500 bg-clip-text text-transparent">Technical Interviews</span>
          </h2>
        </motion.div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 sm:gap-6 max-w-7xl mx-auto">
          {modules.map((module, index) => (
            <ModuleCard key={module.id} module={module} index={index} />
          ))}
        </div>

        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={isInView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.6, delay: 0.8 }}
          className="mt-16 text-center"
        >
          <div className="inline-flex items-center gap-2 px-6 py-3 rounded-full bg-blue-50 border-2 border-blue-200 text-blue-700 font-medium cursor-pointer relative overflow-hidden group">
            {/* Sliding blue background */}
            <div className="absolute inset-0 bg-blue-500 rounded-full transform -translate-x-full group-hover:translate-x-0 transition-transform duration-300 ease-out"></div>

            {/* Content */}
            <div className="w-2 h-2 bg-blue-500 group-hover:bg-white rounded-full animate-pulse relative z-10 transition-colors duration-300"></div>
            <span className="relative z-10 group-hover:text-white transition-colors duration-300">
              <span className="group-hover:hidden">Ready to ace your interview?</span>
              <span className="hidden group-hover:inline">Get started for free</span>
            </span>
          </div>
        </motion.div>
      </div>
    </section>
  );
}

