"use client";

import { useState, use } from "react";
import Link from "next/link";
import { motion, AnimatePresence } from "framer-motion";
import { ArrowLeft, RotateCw, ChevronLeft, ChevronRight, Sparkles } from "lucide-react";
import { questionBank, type FlashcardQuestion } from "@/lib/questionBank";
import { Button } from "@/components/ui/button";
import { Card } from "@/components/ui/card";
import { Progress } from "@/components/ui/progress";
import { Badge } from "@/components/ui/badge";

function shuffleArray<T>(array: T[]): T[] {
  const newArray = [...array];
  for (let i = newArray.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [newArray[i], newArray[j]] = [newArray[j], newArray[i]];
  }
  return newArray;
}

export default function FlashcardsPage({ params }: { params: Promise<{ id: string }> }) {
  const { id } = use(params);
  const [questions, setQuestions] = useState<FlashcardQuestion[]>([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isFlipped, setIsFlipped] = useState(false);
  const [isStarted, setIsStarted] = useState(false);

  const moduleQuestions = questionBank[id];

  const startRound = () => {
    if (moduleQuestions?.flashcards) {
      const shuffled = shuffleArray(moduleQuestions.flashcards);
      setQuestions(shuffled.slice(0, 10));
      setCurrentIndex(0);
      setIsFlipped(false);
      setIsStarted(true);
    }
  };

  const handleNext = () => {
    if (currentIndex < questions.length - 1) {
      setCurrentIndex(currentIndex + 1);
      setIsFlipped(false);
    }
  };

  const handlePrevious = () => {
    if (currentIndex > 0) {
      setCurrentIndex(currentIndex - 1);
      setIsFlipped(false);
    }
  };

  const handleFlip = () => {
    setIsFlipped(!isFlipped);
  };

  if (!moduleQuestions) {
    return <div>Module not found</div>;
  }

  if (!isStarted) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-blue-50">
        <div className="container mx-auto px-4 py-12">
          <motion.div
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            className="mb-8"
          >
            <Link href={`/module/${id}`}>
              <Button variant="outline" className="gap-2">
                <ArrowLeft className="w-4 h-4" />
                Back to Module
              </Button>
            </Link>
          </motion.div>

          <div className="max-w-2xl mx-auto text-center">
            <motion.div
              initial={{ scale: 0.5, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              transition={{ duration: 0.5, type: "spring" }}
              className="text-8xl mb-8"
            >
              🎴
            </motion.div>
            <motion.h1
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.2 }}
              className="text-5xl font-bold text-slate-900 mb-4 bg-gradient-to-r from-blue-600 to-blue-400 bg-clip-text text-transparent"
            >
              Flashcards
            </motion.h1>
            <motion.p
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ delay: 0.3 }}
              className="text-xl text-slate-600 mb-12"
            >
              Review key concepts with 10 random flashcards
            </motion.p>

            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.4 }}
            >
              <Card className="p-8 mb-8 border-2 shadow-xl bg-white/80 backdrop-blur-sm">
                <h2 className="text-2xl font-semibold mb-6 text-blue-600">How it works:</h2>
                <ul className="text-left space-y-4 text-slate-700">
                  {[
                    "You'll see 10 random flashcards from this module",
                    "Click the card to flip and see the answer",
                    "Use the navigation buttons to move between cards",
                    "Start a new round anytime to get different questions"
                  ].map((text, i) => (
                    <motion.li
                      key={i}
                      initial={{ opacity: 0, x: -20 }}
                      animate={{ opacity: 1, x: 0 }}
                      transition={{ delay: 0.5 + i * 0.1 }}
                      className="flex items-start gap-3"
                    >
                      <Badge className="mt-0.5">{i + 1}</Badge>
                      <span>{text}</span>
                    </motion.li>
                  ))}
                </ul>
              </Card>

              <Button onClick={startRound} size="lg" className="gap-2 shadow-lg">
                <Sparkles className="w-5 h-5" />
                Start Flashcards
              </Button>
            </motion.div>
          </div>
        </div>
      </div>
    );
  }

  const currentQuestion = questions[currentIndex];
  const progress = ((currentIndex + 1) / questions.length) * 100;

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-blue-50">
      <div className="container mx-auto px-4 py-12">
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          className="mb-8 flex justify-between items-center"
        >
          <Link href={`/module/${id}`}>
            <Button variant="outline" className="gap-2">
              <ArrowLeft className="w-4 h-4" />
              Back
            </Button>
          </Link>
          <Button onClick={startRound} variant="secondary" className="gap-2">
            <RotateCw className="w-4 h-4" />
            New Round
          </Button>
        </motion.div>

        <div className="max-w-4xl mx-auto">
          <motion.div
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            className="mb-8"
          >
            <div className="flex items-center justify-between mb-3">
              <Badge variant="secondary" className="text-sm">
                Card {currentIndex + 1} of {questions.length}
              </Badge>
              <span className="text-sm text-slate-600 font-medium">
                {Math.round(progress)}% Complete
              </span>
            </div>
            <Progress value={progress} className="h-3" />
          </motion.div>

          <div className="mb-8">
            <AnimatePresence mode="wait">
              <motion.div
                key={`${currentIndex}-${isFlipped}`}
                initial={{ opacity: 0, scale: 0.95 }}
                animate={{ opacity: 1, scale: 1 }}
                exit={{ opacity: 0, scale: 0.95 }}
                transition={{ duration: 0.2 }}
                onClick={handleFlip}
                className="cursor-pointer"
              >
                <Card className="border-2 hover:border-blue-300 transition-all shadow-2xl bg-gradient-to-br from-white to-blue-50/30 backdrop-blur-sm overflow-hidden">
                  <div className="absolute inset-0 bg-gradient-to-br from-blue-500/5 to-transparent"></div>
                  <div className="relative z-10 p-12 min-h-[400px] flex flex-col items-center justify-center text-center">
                    {!isFlipped ? (
                      <>
                        <Badge className="mb-6 bg-blue-600">QUESTION</Badge>
                        <p className="text-3xl text-slate-900 font-medium leading-relaxed">
                          {currentQuestion.question}
                        </p>
                        <div className="mt-12 flex items-center gap-2 text-blue-600 font-medium animate-pulse">
                          <RotateCw className="w-4 h-4" />
                          <span className="text-sm">Click to reveal answer</span>
                        </div>
                      </>
                    ) : (
                      <>
                        <Badge className="mb-6 bg-green-600">ANSWER</Badge>
                        <p className="text-3xl text-slate-900 leading-relaxed">
                          {currentQuestion.answer}
                        </p>
                        <div className="mt-12 flex items-center gap-2 text-slate-500 font-medium">
                          <RotateCw className="w-4 h-4" />
                          <span className="text-sm">Click to see question</span>
                        </div>
                      </>
                    )}
                  </div>
                </Card>
              </motion.div>
            </AnimatePresence>
          </div>

          <div className="flex justify-between items-center gap-4">
            <Button
              onClick={handlePrevious}
              disabled={currentIndex === 0}
              variant="outline"
              size="lg"
              className="gap-2"
            >
              <ChevronLeft className="w-5 h-5" />
              Previous
            </Button>

            <Button
              onClick={handleFlip}
              variant="secondary"
              size="lg"
              className="gap-2"
            >
              <RotateCw className="w-5 h-5" />
              {isFlipped ? "Show Question" : "Show Answer"}
            </Button>

            <Button
              onClick={handleNext}
              disabled={currentIndex === questions.length - 1}
              variant="outline"
              size="lg"
              className="gap-2"
            >
              Next
              <ChevronRight className="w-5 h-5" />
            </Button>
          </div>

          {currentIndex === questions.length - 1 && (
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              className="mt-8"
            >
              <Card className="p-8 text-center border-2 border-green-200 bg-gradient-to-br from-green-50 to-white shadow-xl">
                <div className="text-5xl mb-4">🎉</div>
                <p className="text-green-800 font-semibold text-xl mb-6">
                  You've completed all flashcards in this round!
                </p>
                <Button onClick={startRound} className="gap-2" size="lg">
                  <RotateCw className="w-5 h-5" />
                  Start New Round
                </Button>
              </Card>
            </motion.div>
          )}
        </div>
      </div>
    </div>
  );
}

